import React, { Component } from 'react';

class Form extends Component {
  state = { title: '' }

  render() {
    return (
      <form onSubmit={this.handleSubmit}>
        <input 
          required 
          placeholder='add todo'
          name = 'title'
          value={this.state.title}
          onChange={this.handleChange}
        />
        <button>Submit</button>
      </form>
    )
  }
}

export default Form;
